var isArray = 'undefined' == typeof window
  ? require('..')
  : require('is-array'); // how to do this better?

var assert = require('assert');

describe('isArray', function(){
  it('should test', function(){
    assert(!isArray(undefined), 'undefined vars are not arrays');
    assert(!isArray(arguments), 'the arguments object is not an array');
    assert(isArray([1, 2, 3]), 'but arrays are');
  });
});
